import { Component } from '@angular/core';

@Component({
  selector: 'app-form',
  templateUrl: './form.component.html',
  styleUrls: ['./form.component.css']
})
export class FormComponent {

}
<div><link rel="stylesheet" href="https://cdn.jsdelivr.net/npm/bootstrap@4.0.0/dist/css/bootstrap.min.css" integrity="sha384-Gn5384xqQ1aoWXA+058RXPxPg6fy4IWvTNh0E263XmFcJlSAwiGgFAW/dAiS6JXm" crossorigin="anonymous">
<form>
    <label> Full Name: </label>
    <input type="text" id="name" name="name"><br>
    <label> Email: </label>
    <input type="text" id="email" name="email"><br>
    <label> Feedback:  </label>
    <input type="textarea" id="feedback" name="feedback"><br>

    <button type="submit">Submit</button>

</form>
