import { Component } from '@angular/core';
import { NgForm } from '@angular/forms';

@Component({
  selector: 'app-form',
  templateUrl: './form.component.html',
  styleUrls: ['./form.component.css']
})
export class FormComponent {

  onSubmit(f:NgForm){
    console.log(f);
  }

}
<div class="container mt-5">
<div class="form-area">
<form #f="ngForm" (submit)="onSubmit(f)">
    <div class="form-group">
    <label> Full Name: </label>
    <input type="text" class="form-control" id="name" name="fullName" ngModel #fullName="ngModel" (change)="getValue(fullName)"><br>
    <label> Email: </label>
    <input type="text" class="form-control" id="email" name="email" ngModel><br>
    <label> Address:  </label>
    <input type="textarea" class="form-control" id="address" name="address" ngModel><br>

    <button type="submit" class="btn btn-primary" >Submit</button>
    </div>

</form>
</div>
</div>
